-- ----------------------------
-- Table structure for sys_user
-- ----------------------------
DROP TABLE IF EXISTS `sys_user`;
CREATE TABLE `sys_user`  (
  `user_id` bigint NOT NULL COMMENT '主键id',
  `login_id` varchar(45) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '登录Id',
  `password` varchar(45) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '密码',
  `username` varchar(45) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '名字',
  `email` varchar(45) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '电子邮件',
  `mobile` varchar(45) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '手机',
  `uuid` varchar(45) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'UUID',
  `admin` int NULL DEFAULT 0 COMMENT '是否管理员',
  `avatar` mediumtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '头像',
  `status` bigint NULL DEFAULT NULL COMMENT '状态',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `create_by` bigint NULL DEFAULT NULL COMMENT '创建人',
  `last_modified_time` datetime NULL DEFAULT NULL COMMENT '最后的更新时间',
  `last_modified_by` bigint NULL DEFAULT NULL COMMENT '最后的更新人',
  `version` int NULL DEFAULT 0 COMMENT '乐观锁',
  PRIMARY KEY (`user_id`) USING BTREE,
  INDEX `IX_SYS_USER_LOGINID`(`login_id`) USING BTREE,
  INDEX `IX_SYS_USER_USERNAME`(`username`) USING BTREE,
  INDEX `IX_SYS_USER_UUID`(`uuid`) USING BTREE,
  INDEX `IX_SYS_USER_MOBILE`(`mobile`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '用户表' ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of sys_user
-- ----------------------------

-- ----------------------------
-- Table structure for talk
-- ----------------------------
DROP TABLE IF EXISTS `talk`;
CREATE TABLE `talk`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `title` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '标题',
  `description` varchar(1024) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '描述',
  `tag` varchar(128) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '标签',
  `images` varchar(2048) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '图片地址',
  `video` varchar(128) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '视频地址',
  `type` int NOT NULL DEFAULT 1 COMMENT '类型',
  `status` int NOT NULL DEFAULT 0 COMMENT '状态',
  `permission` int NOT NULL DEFAULT 0 COMMENT '权限',
  `creator` bigint NOT NULL COMMENT '发布者',
  `view_count` int NOT NULL DEFAULT 0 COMMENT '浏览数',
  `comment_count` int NOT NULL DEFAULT 0 COMMENT '评论数',
  `like_count` int NOT NULL DEFAULT 0 COMMENT '点赞数',
  `gmt_latest_comment` bigint NOT NULL COMMENT '最后评论时间',
  `gmt_create` bigint NOT NULL COMMENT '创建时间',
  `gmt_modified` bigint NOT NULL COMMENT '修改时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 55 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = DYNAMIC;

